package javaexp.a01_start;

public class A07_SemiColon {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수 선언, 값의 저장, 메소드 호출 등
		// 자바의 모든 명령어의 마지막에는 ;(세미콜론)을 붙힌다.
		int num01;
		num01 = 25;
		int num02 = 30;
		int num03 = num01 + num02;
		int age01 = 25; int age02 = 17; // 명령어를 세미콜론을 통해 연속적으로 선언하고 할당할 수 있다.
		System.out.println(num03);
		System.out.println(age01+age02);
	}

}
