package javaexp.a02_start2;

public class A03_VarinitExp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// ex) 현재 나이를 변수로 선언하여 초기화가 되지 않았을때, 10년 후 나이를 처리해보고
		//		초기화 한 후, 10년 후 나이를 처리하고 출력하세요
		int age;
//		System.out.println("초기화전 10년 후 나이: "+(age+10));
		age = 28;
		System.out.println("초기화 후 현재 나이: "+age);
		System.out.println("초기화 후 10년 후 나이: "+(age+10));
	}

}
